import { useRef, useState, useEffect } from "react";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);
  const inputRef = useRef();

  // ✅ عند تحميل التطبيق، استرجعي المهام من localStorage
  useEffect(() => {
    const savedTodos = localStorage.getItem("todos");
    if (savedTodos) {
      setTodos(JSON.parse(savedTodos));
    }
  }, []);

  // ✅ كلما تغيّرت المهام، خزّنيها في localStorage
  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const handleAddTodo = () => {
    const text = inputRef.current.value.trim();
    if (!text) return; // تجاهل الإدخال الفارغ

    const newItem = { completed: false, text };
    setTodos([...todos, newItem]);
    inputRef.current.value = "";
  };

  const handleItemDone = (index) => {
    const newTodos = [...todos];
    newTodos[index].completed = !newTodos[index].completed;
    setTodos(newTodos);
  };

  const handleDeleteItem = (index) => {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  };

  return (
    <div className="App">
      <h1>قائمة المهام</h1>
      <div className="to-do-container">
        <ul>
          {todos.map(({ text, completed }, index) => (
            <div key={index} className="item">
              <span onClick={() => handleDeleteItem(index)} className="trash">X</span>
              <input
                type="checkbox"
                checked={completed}
                onChange={() => handleItemDone(index)}
              />
              <li className={completed ? "done" : ""}>{text}</li>
            </div>
          ))}
        </ul>
        <input ref={inputRef} placeholder="أضف مهمتك هنا" />
        <button onClick={handleAddTodo}>إضافة مهمة</button>
      </div>
    </div>
  );
}

export default App;
