import { useRef, useState, useEffect } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import Settings from "./Settings";
import "./App.css";

function Home() {
  const inputRef = useRef();

  const [todos, setTodos] = useState(() => {
    const saved = localStorage.getItem("todos");
    return saved ? JSON.parse(saved) : [];
  });

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const handleAddTodo = () => {
    const text = inputRef.current.value.trim();
    if (!text) return;
    const newItem = { completed: false, text };
    setTodos((prevTodos) => [...prevTodos, newItem]);
    inputRef.current.value = "";
  };

  const handleItemDone = (index) => {
    const newTodos = [...todos];
    newTodos[index].completed = !newTodos[index].completed;
    setTodos(newTodos);
  };

  const handleDeleteItem = (index) => {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      handleAddTodo();
    }
  };

  const navigate = useNavigate();

  return (
    <div className="App">
      <h1>قائمة المهام</h1>
      <div className="to-do-container">
        <ul>
          {todos.map(({ text, completed }, index) => (
            <div className="item" key={index}>
              <input
                type="checkbox"
                checked={completed}
                onChange={() => handleItemDone(index)}
              />
              <li className={completed ? "done" : ""}>{text}</li>
              <span onClick={() => handleDeleteItem(index)} className="trash">
                X
              </span>
            </div>
          ))}
        </ul>
        <input
          type="text"
          ref={inputRef}
          onKeyDown={handleKeyPress}
          placeholder="أدخل مهمة جديدة..."
        />
        <button onClick={handleAddTodo}>إضافة مهمة</button>
        <br />
        <button onClick={() => navigate("/settings")} className="settings-btn">
          ⚙️ الذهاب إلى الإعدادات
        </button>
      </div>
    </div>
  );
}

function App() {
  return (
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="/settings" element={<Settings />} />
    </Routes>
  );
}

export default App;
